Export.CSP:
'use strict'; 

angular.module('recruting',['ngCookies']).
    config(['$routeProvider',function($routeProvider) {
        $routeProvider.

            when('/admin/response/:vacID',{templateUrl: 'partials/response.html', controller:vrShow}).
            when('/admin',{templateUrl: 'partials/vacancies.html', controller:vacancyCtrl}).

            when('/admin/users',{templateUrl: 'partials/admin_users.html', controller:adminUsersCtrl}).
            when('/admin/user',{templateUrl: 'partials/admin_user.html', controller:adminUserCtrl}).

            when('/admin/companies',{templateUrl: 'partials/admin_companies.html', controller:CompanyShow}).
            when('/admin/company/:compID',{templateUrl: 'partials/admin_company.html', controller:CompanyShow}).

            when('/admin/vacancy/:vacID',{templateUrl: 'partials/admin_vacancy.html', controller:adminVacancyCtrl}).

            when('/vacancies',{templateUrl: 'partials/vacancies.html', controller:vacancyCtrl}).
            when('/vacancy/:vacID', {templateUrl: 'partials/vacancy.html', controller:vacancyShowCtrl}).
            when('/company/:compID',{templateUrl: 'partials/company.html', controller:companyCtrl}).
            otherwise({redirectTo: '/vacancies'});
    }]);
